import React, { useState } from 'react'

const Header = (props) => {

  return (
    <div>
      <h1>{props.title}</h1>
    </div>
  )}

const Subheader = (props) => {
  return (
    <div>
      <h2>{props.subtitle}</h2>
    </div>
  )
}
const Statistics = (props) => {
  return (
    <div>
      <div>all {props.good + props.neutral + props.bad}</div>
      <div>average {(props.good - props.bad) / (props.good + props.neutral + props.bad)}</div>
      <div>positive {props.good / (props.good + props.neutral + props.bad) * 100}%</div>
    </div>
  )
}

const History = (props) => {
  if(props.good === 0 && props.neutral === 0 && props.bad === 0)  {
    return (
      <div>
        No feedback given
      </div>
    )
  }
  return (
    <div>

    </div>
  )  
} 

const Button = (props) => (
  <button onClick={props.handleClick}>{props.text}</button>
)







const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const title = 'give feedback'
  const subtitle = 'statistics' 
  const setToGood = (newGood) => {
    setGood(newGood)
  }
  const setToNeutral = (newNeutral) => {
    setGood(newNeutral)
  }
  const setToBad = (newBad) => {
    setGood(newBad)
  }


  return (
    <div>
      <Header title={title} />
      <div>
      <Button handleClick={() => useState + 1} text="good" />
      <Button handleClick={() => setToNeutral(0)} text="neutral" />
      <Button handleClick={() => setToBad(0)} text="bad" />
      </div>
      <div>
        <Subheader subtitle={subtitle} />
      </div>
      <div>
        Good {good}
      </div>
      <div>
        Neutral {neutral}
      </div>
      <div>
        Bad {bad}
      </div>
      <div>
        <Statistics good={good} neutral={neutral} bad={bad} />
      </div>
      <div>
        <History good={good} neutral={neutral} bad={neutral} />
      </div>
    </div>
  )
}

export default App
