import React, { useState } from 'react'

const Header = (props) => {

  return (
    <div>
      <h1>{props.title}</h1>
    </div>
  )}

const Subheader = (props) => {
  return (
    <div>
      <h2>{props.subtitle}</h2>
    </div>
  )
}
const Statistics = (props) => {

  return (
    <div>
      <Statistic text="good" value ={props.good} />
      <Statistic text="neutral" value={props.neutral} />
      <Statistic text="bad" value={props.bad} />
      <Statistic text="all" value={props.all}/>
      <Statistic text="average" value={(props.good - props.bad) / (props.good + props.neutral + props.bad)} />
      <Statistic text="positive" value={props.good / (props.good + props.neutral + props.bad) * 100}/>
    </div>
  )
}

const History = (props) => {
  if(props.good === 0 && props.neutral === 0 && props.bad === 0)  {
    return (
      <div>
        No feedback given
      </div>
    )
  }
  return (
    <div>

    </div>
  )  
} 

const Button = ({handleClick, text}) => (
  <button onClick={handleClick}>
    {text}
  </button>
)

const Statistic = (props) => {
  return (
    <tr>
      <td>{props.text}</td>
      <td>{props.value}</td>
    </tr>
  )
}







const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const all = good + neutral + bad
  const title = 'give feedback'
  const subtitle = 'statistics' 
  const setToGood = () => {
    setGood(good + 1)
  }
  const setToNeutral = () => {
    setNeutral(neutral + 1)
  }
  const setToBad = () => {
    setBad(bad + 1)
  }



  return (
    <div>
      <Header title={title} />
      <div>
      <Button handleClick={setToGood} text="good" />
      <Button handleClick={setToNeutral} text="neutral" />
      <Button handleClick={setToBad} text="bad" />
      </div>
      <div>
        <Subheader subtitle={subtitle} />
      </div>

      <div>
        <Statistics good={good} neutral={neutral} bad={bad} />

      </div>

      <div>
        <History good={good} neutral={neutral} bad={neutral} />
      </div>
    </div>
  )
}

export default App
