import React from 'react'

const Header = (props) => {

  return (
    <div>
      <h1>{props.name}</h1>
    </div>
  )
}
const Part = (props) => {


  return (
    <div>
      {props.name}{props.exercises}
    </div>
  )
}
const Content = ({parts}) => {
  const contents = parts.map(part => {

  return (
    <Part name={part.name}exercises={part.exercises}key={part.id}/>
  )

})
}
const Total = ({parts}) => {
  const total = parts.reduce((sum, currentNumber) => sum + currentNumber.exercises, 0)
  return (
    <div>
      <p>Total of {total} exercises</p>
    </div>
  )
  }

const Course = ({course}) => {
  return (
    <div>
      <Header name={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  )

}
const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]
  const classes = courses.map(course => {
    return <Course key={course.id} course={course} />
  })
  return (
    <div>
      {classes}
    </div>
  )
}

export default App
