const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const addName = (event) => {
    event.preventDefault()
    setNewName(event.target.value)
    const nameObject = {
      content: newName,
      id: newName
    }
    setPersons(persons.concat(nameObject))
    setNewName('')
  }



  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addName}>
        <div>
          name: <input 
          value={newName} 

            />
        </div>
        <div>

          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>

      <div>{newName}</div>

    </div>
  )
}

export default App
