import React from 'react'

const Header = (props) => {

  return (

      <h1>{props.course}</h1>

  )
}
const Content = () => {
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }
  return (
    <div>
      <p>{props.part1}</p> 
      <p>{props.part2}</p>
      <p>{props.part3}</p>
    </div>

  )
}



const Total = (props) => {

  return (
    <div>
      <p>Number of exercises {props.part1.exercises} + {props.part2.exercises} + {props.part3.exercises}</p>
    </div>
  )
}
const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course}/>
      <Content part1={part1}/>

    </div>
  )
}

export default App
