import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Button = ({handleClick, text}) => (
  <button onClick={handleClick}>
    {text}
  </button>
)

const App = () => {
  useEffect(() => {
    axios.get('https://restcountries.eu/rest/v2/all')
    .then(response => {
    console.log(response.data)
    setCountries(response.data)
    })
  }, [])

  const [countries, setCountries] = useState([])

  const [showAll, setShowAll] = useState('')

  const [styleChange, setStyleChange] = useState("display:none")

  const countriesToShow = 
      countries.filter(country => country.name.toLowerCase().includes(showAll.toLowerCase()))

  const handleCountryChange = (event) => {
    setShowAll(event.target.value)
    }




  if (countriesToShow.length > 10) {
      return (
        <div>
        find countries <input
        value = {showAll}
        onChange={handleCountryChange}/>

        <p>Too many matches, specify another filter</p>
        </div>
      )
    }
  else {
    return (
      <div>
        find countries <input
        value = {showAll}
        onChange={handleCountryChange}/>


        {countriesToShow.map(country => (
          <>
            <h2 key={country.name}>{country.name}</h2><Button handleClick={setStyleChange("display:block")} text="show" />

            <div key={country.alpha3Code} style={styleChange}> 
            <h2 key={country.name}>{country.name}</h2>
            <br />
            <div>
              capital {country.capital}
              <br />
              population {country.population}
            </div>
            <h3>
              languages
            </h3>
            <ul>
              {country.languages.map(lang => 
              <li key={lang.name}>{lang.name}</li>
              )}
            </ul>
            <img key={country.alpha2Code} src={country.flag} alt="Country Flag" width="200" height="100"/>

            </div>
            </>
          )
        )}
      </div>
    )
  }
}

export default App
